package com.nisharp.web.infrastructure.util;

import org.apache.commons.codec.binary.Base64;
import org.junit.Assert;
import org.junit.Test;

import javax.imageio.ImageIO;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.FileOutputStream;

/**
 * Created by ZM.Wang
 */
public class QrcodeUtilsTest {

    private final static String logoBase64 = "";

    @Test
    public void createSimpleQrcode() throws Exception {
        BufferedImage image = QrcodeUtils.createSimpleQrcode("This is test content,This is test content,This is test content,This is test content", 500, 0, new Color(0, 0, 0), new Color(255, 255, 255, 0));
        try (FileOutputStream outputStream = new FileOutputStream("d:/simple.png")) {
            try (ByteArrayOutputStream file = QrcodeUtils.imageToStream(image)) {
                outputStream.write(file.toByteArray());
            }
        }
        Assert.assertNotNull(image);
    }

    @Test
    public void getComplexQrcode() throws Exception {
        ByteArrayInputStream logo = new ByteArrayInputStream(Base64.decodeBase64(logoBase64));
        BufferedImage srcImage = ImageIO.read(logo);
        BufferedImage image = QrcodeUtils.getComplexQrcode("This is test content,This is test content,This is test content,This is test content", 500, srcImage, 0, new Color(1, 255, 1), new Color(255, 255, 255, 0));
        try (FileOutputStream outputStream = new FileOutputStream("d:/Complex.png")) {
            try (ByteArrayOutputStream file = QrcodeUtils.imageToStream(image)) {
                outputStream.write(file.toByteArray());
            }
        }
        Assert.assertNotNull(image);
    }

}